function si_slider_get_uniq(uniq, text, space) {
	
	if (space == undefined) space = true;
	
	if (uniq) {
		if (space)
			return ' ' + uniq + text;
		else
			return uniq + text;
	}else{
		return '';
	}
	
}

function si_slider_get_name(name, text) {

	if (name) {
		return '' + name + text;
	}else{
		return '';
	}
	
}

jQuery(function($) {
	
	$.fn.si_slider = function(options){
	
		var settings = $.extend({
				'on_screen' : 1,
				'by_tap' : 1,
				'speed' : 400,
				'uniq' : false,
				'class_name' : 'si-slider',
				'bullets' : false,
					'bullets_arrows' : false,
					'bullets_side' : false,
				'arrows' : true,
				'first_slide' : 0
			}, options);
		
		
		return this.each(function() {
			
			// Slider 
			var $slider = $(this);
			
			// Slider name
			var slider_name = $slider.attr('class')
			if (!settings.uniq) settings.uniq = slider_name;
			
			// Parameters
			var slider_size = $slider.find('.' + slider_name + '-item').size();
			var slide_width = $slider.find('.' + slider_name + '-item').first().outerWidth(true);

				// Set
				$slider.data('on_screen', settings.on_screen);
				$slider.data('by_tap', settings.by_tap);
				$slider.data('current', settings.first_slide);
				//$slider.data('slider_size', slide_width);
			
			
			// Functions
			
			function si_slider_restyle() {
			
				var current = $slider.data('current');

				// Restyle bullets
				var $bullets = $('.' + si_slider_get_uniq(settings.uniq, '-bullet', false));
					$bullets.removeClass('current');
					$bullets.eq(current / settings.by_tap).addClass('current');
				
				// @todo inactive
				
			}
			
			function si_slider_slide_to(number) {
			
				if (number == 0) {
					$slider.animate({'left' : 0}, settings.speed)
				}else{
					$slider.animate({'left' : number * -slide_width}, settings.speed);
				}
				
				$slider.data('current', number);
				si_slider_restyle();
				
				return false;
				
			}

				
				
				// Generate HTML-wrappers
				$slider.wrap('<div class="' + si_slider_get_name(settings.class_name, '-wrapper') + si_slider_get_uniq(settings.uniq, '-wrapper') + '"></div>');
					var $wrap = $slider.parent();
				$wrap.wrap('<div class="' + si_slider_get_name(settings.class_name, '-wrapper-2') + si_slider_get_uniq(settings.uniq, '-wrapper-2') + '"></div>');
					$wrap = $wrap.parent();
					
				// Add arrows
				if (settings.arrows) {
					$wrap.append('<a href="#" class="' + si_slider_get_name(settings.class_name, '-arrow l') + si_slider_get_uniq(settings.uniq, '-arrow l') + '">');
					$wrap.append('<a href="#" class="' + si_slider_get_name(settings.class_name, '-arrow r') + si_slider_get_uniq(settings.uniq, '-arrow r') + '">');
				}
				
				// Add bullets
				if (settings.bullets) {
					
					// Bullets wrap
					var $bullets = $('<div class="'+ si_slider_get_name(settings.class_name, '-bullets') + si_slider_get_uniq(settings.uniq, '-bullets') + '"></div>').appendTo($wrap);
					
					// Side left
					if (settings.bullets_side) {
					
						$bullets.append('<a href="#" class="'+ si_slider_get_name(settings.class_name, '-bullet-arrow-side l') + si_slider_get_uniq(settings.uniq, '-bullet-arrow-side l') + '">1</a>');
					
					}
					
					// Arrow left
					if (settings.bullets_arrows) {
					
						$bullets.append('<a href="#" class="'+ si_slider_get_name(settings.class_name, '-bullet-arrow l') + si_slider_get_uniq(settings.uniq, '-bullet-arrow l') + '">1</a>');
					
					}
					
					// Bullets
					var l = Math.ceil(slider_size / settings.by_tap);
					for (var i = 0; i < l; i++) {
						var counter = i + 1;
						$bullets.append('<a href="#" class="'+ si_slider_get_name(settings.class_name, '-bullet') + si_slider_get_uniq(settings.uniq, '-bullet') + '">' + counter + '</a>');
					}
						
						// Set active
						$('.' + si_slider_get_uniq(settings.uniq, '-bullet', false)).eq(settings.first_slide).addClass('current');
					
					// Arrow right
					if (settings.bullets_arrows) {
					
						$bullets.append('<a href="#" class="'+ si_slider_get_name(settings.class_name, '-bullet-arrow r') + si_slider_get_uniq(settings.uniq, '-bullet-arrow r') + '">1</a>');
					
					}
					
					// Side right
					if (settings.bullets_side) {
					
						$bullets.append('<a href="#" class="'+ si_slider_get_name(settings.class_name, '-bullet-arrow-side r') + si_slider_get_uniq(settings.uniq, '-bullet-arrow-side r') + '">' + slider_size + '</a>');
					
					}		
					
					
					// Bullets arrows click
					$('.' + si_slider_get_uniq(settings.uniq, '-bullet-arrow', false)).click(function() {
						
						var current = $slider.data('current');
						
						if ($(this).hasClass('l')) {
							
							if (current != 0) {
								si_slider_slide_to((current-1) * settings.by_tap);
							}else{
								si_slider_slide_to((slider_size-1) / settings.by_tap);
							}
							
						}else{
						
							if (current != slider_size - 1) {
								si_slider_slide_to(current+1);
							}else{
								si_slider_slide_to(0);
							}
						
						}
						
						return false;
						
					});	
					
					// Bullets click
					$('.' + si_slider_get_uniq(settings.uniq, '-bullet', false)).click(function() {
						
						var $bullets = $('.' + si_slider_get_uniq(settings.uniq, '-bullet', false));
						var index = $bullets.index($(this));
						si_slider_slide_to(index * settings.by_tap);
						
						return false;
						
					});
					
				}

			
		})
		
	}

})










